function counterclockresponse = getaresponse;while 1	[keyisdown,when,keyCode] = KbCheck;	if keyisdown		if strcmp(KbName(keyCode),'left') % left arrow			counterclockresponse = 1;			break;		elseif strcmp(KbName(keyCode),'right') % right arrow			counterclockresponse = 0;            break        elseif strcmp(KbName(keyCode),'q') % q button            counterclockresponse = 0;            screen closeall;			return;		end	endend