clear all;for f = 1:3    load(strcat(num2str(f), '.mat'));    subplot(3,1,f);    for i = 1:2        intensities = stair(i).intensity;        switchtrials = stair(i).switchtrial;        switchintensities = intensities(switchtrials);                plot(intensities)        hold on;        plot(switchtrials,switchintensities,'o', 'Color', [0 0 0]);        title('fancy pants graph');        xlabel('switchtrials');        ylabel('switchintensities');    end    hold off;end