clear all;means = [];standarts = [];for f = 1:3    load(strcat(num2str(f), '.mat'));    subplot(4,1,f);    for i = 1:2        intensities = stair(i).intensity;        switchtrials = stair(i).switchtrial;        switchintensities = intensities(switchtrials);        try            means(f) = cat(2, means(f), mean(switchintensities));            standarts(f) = cat(2, standarts(f), std(switchintensities));        catch            means(f) = mean(switchintensities);            standarts(f) = std(switchintensities);        end                        plot(intensities)        hold on;        plot(switchtrials,switchintensities,'o', 'Color', [0 0 0]);        title('fancy pants graph');        xlabel('switchtrials');        ylabel('switchintensities');    end    hold off;endsubplot(4,1,4);bar(means);hold on;errorbar([1 2 3], means, standarts, 'o');set(gca, 'xtick', [1 2 3]);set(gca, 'xticklabel', {'horizontal', 'vertical', 'oblique'});hold off;