load('results.mat');subplot(2,1,1);plot(results);    title('raw data');xlabel('experiment number');ylabel('time');          means = [];standarts = [];for i = 1:length(results(1,:))    means(i) = mean(results(:,i));    standarts(i) = std(results(:,i));end          subplot(2,1,2);bar(means);title('Average duration');hold on;errorbar([1 2 3], means, standarts, 'o');set(gca, 'xtick', [1 2 3]);set(gca, 'xticklabel', {'first', 'second', 'third'});hold off;  