function counterclockresponse = getaresponse;trap = 0;while trap == 0	[keyisdown,when,keyCode] = KbCheck;	if keyisdown		if strcmp(KbName(keyCode),'Left') % left arrow			counterclockresponse = 1;			break;		elseif strcmp(KbName(keyCode),'Right') % right arrow			counterclockresponse = 0;            break;        end	endendreturn;